void HomeScreenWithBiodataViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &buttonfornothing)
    {
        //Interaction1
        //When buttonfornothing clicked change screen to swipedownfromHome
        //Go to swipedownfromHome with screen transition towards North
        application().gotoswipedownfromHomeScreenCoverTransitionNorth();
    }
    if (&src == &hr_detail_button)
    {
        //Interaction3
        //When hr_detail_button clicked change screen to HeartrateDetail
        //Go to HeartrateDetail with screen transition towards East
        application().gotoHeartrateDetailScreenCoverTransitionEast();
    }
    if (&src == &spo2_detail_button)
    {
        //Interaction4
        //When spo2_detail_button clicked change screen to Spo2Detail
        //Go to Spo2Detail with screen transition towards East
        application().gotoSpo2DetailScreenCoverTransitionEast();
    }
}
